		idd = -1; duration = 10e10; fadein = 0; fadeout = 0; name = "loading screen"; class controlsBackground { class blueBackground : RscText // covers the black screen { x = safezoneXAbs; y = safezoneY; w = safezoneWAbs; h = safezoneH; text = ""; colorText[] = { 0, 0, 0, 0 
